import org.slf4j.MDC;

public class SomeClass {
     public void process(Order order) {
         try {
             MDC.put("order", order.getId());
             MDC.put("customer", order.getCustomerId());
             LOG.debug("Order {} initiated for {}", order.geId(), order.getCustomerId());

             // .. business logic
         } finally {
             MDC.remove("order");
             MDC.remove("customer");
         }
    }
}

